const tabUnderlineActiveDarkMode = style(colorModeStyle({
  darkMode: {
    background: vars.borderColor.formAccentLight
  }
}), "tabUnderlineActiveDarkMode");
const tabPanel = style({}, "tabPanel");
const tabPanelFocusRing = style({
  selectors: {
    [`${tabPanel}:focus > &`]: {
      opacity: 1
    }
  }
}, "tabPanelFocusRing");
const divider = style({
  height: vars.borderWidth.standard
}, "divider");
endFileScope();
export {
  cropToIconX,
  divider,
  hoveredTab,
  marginAuto,
  mask,
  nowrap,
  scroll,
  tab,
  tabFocusRing,
  tabPanel,
  tabPanelFocusRing,
  tabUnderline,
  tabUnderlineActiveDarkMode,
  underlineLeft,
  underlineWidth
};
