  overflowWrap: "break-word"
}, "headingRoot");
const headingFocus = style({
  selectors: {
    [`${headingRoot}:focus &`]: {
      opacity: 1
    }
  }
}, "headingFocus");
const closeIconOffset = style({
  top: "-5px",
  right: "-5px"
}, "closeIconOffset");
endFileScope();
