  backdrop,
  closeIconOffset,
  entrance,
  exit,
  fixedStackingContext,
  headingFocus,
  headingRoot,
  horiztontalTransition,
  maxSize,
  modalContainer,
  pointerEventsAll,
  resetStackingContext
