"use strict";
const fileScope = require("@vanilla-extract/css/fileScope");
const css = require("@vanilla-extract/css");
fileScope.setFileScope("src/lib/components/Alert/Alert.css.ts?used", "braid-design-system");
const closeButton = css.style({}, "closeButton");
const closeButtonFocus = css.style({
  selectors: {
    [`${closeButton}:focus &`]: {
      opacity: 1
    }
  }
}, "closeButtonFocus");
const closeButtonHover = css.style({
  selectors: {
    [`${closeButton}:focus &, ${closeButton}:hover &`]: {
      opacity: 1
    }
  }
}, "closeButtonHover");
fileScope.endFileScope();
exports.closeButton = closeButton;
exports.closeButtonFocus = closeButtonFocus;
exports.closeButtonHover = closeButtonHover;
