"use strict";
const fileScope = require("@vanilla-extract/css/fileScope");
const css = require("@vanilla-extract/css");
const styles_lib_css_responsiveStyle_cjs = require("./responsiveStyle.cjs");
const styles_lib_themes_vars_css_cjs = require("../themes/vars.css.cjs");
fileScope.setFileScope("src/lib/css/lineHeightContainer.css.ts?used", "braid-design-system");
const lineHeightContainer = css.styleVariants(styles_lib_themes_vars_css_cjs.vars.textSize, ({
  mobile,
  tablet
}) => styles_lib_css_responsiveStyle_cjs.responsiveStyle({
  mobile: {
    height: mobile.lineHeight
  },
  tablet: {
    height: tablet.lineHeight
  }
}), "lineHeightContainer");
fileScope.endFileScope();
exports.lineHeightContainer = lineHeightContainer;
